mapper.select:
SELECT eventno, e_title, e_content, e_file1, e_size1, e_thumb1, e_cnt, rdate, word, adminno, productno 
FROM event
WHERE eventno = #{eventno}

mapper.update:
UPDATE event
SET e_title= #{e_title}, e_content= #{e_content},
      e_file1=#{e_file1}, e_thumb1=#{e_thumb1}, e_size1=#{e_size1}, word=#{word}
WHERE eventno =#{eventno}

mapper.select:
SELECT eventno, e_title, e_content, e_file1, e_size1, e_thumb1, e_cnt, rdate, word, adminno, productno, r FROM( SELECT eventno, e_title, e_content, e_file1, e_size1, e_thumb1, e_cnt, rdate, word, adminno, productno, rownum as r FROM( SELECT eventno, e_title, e_content, e_file1, e_size1, e_thumb1, e_cnt, rdate, word, adminno, productno FROM event
WHERE word LIKE '%' ||#{word}||'%'
ORDER BY eventno DESC ) ) WHERE r >= #{startNum} AND r <= #{endNum}
