mapper.update:
UPDATE review
SET r_title= #{r_title}, r_content= #{r_content},
      r_file1=#{r_file1}, r_thumb1=#{r_thumb1}, r_size1=#{r_size1}, 
      rname= #{rname}, word=#{word}, score=#{score}, customerno=#{customerno}
WHERE reviewno =#{reviewno}

mapper.select:
SELECT reviewno, r_title, r_content, r_file1, r_size1, r_thumb1, r_cnt, replycnt, rdate, rname, grpno, indent, ansnum, word, score, productno, customerno, r FROM( SELECT reviewno, r_title, r_content, r_file1, r_size1, r_thumb1, r_cnt, replycnt, rdate, rname, grpno, indent, ansnum, word, score, productno, customerno, rownum as r FROM( SELECT reviewno, r_title, r_content, r_file1, r_size1, r_thumb1, r_cnt, replycnt, rdate, rname, grpno, indent, ansnum, word, score, productno, customerno FROM review
WHERE word LIKE '%' ||#{word}||'%'
ORDER BY grpno DESC, ansnum ASC ) ) WHERE r >= #{startNum} AND r <= #{endNum}

mapper.update:
UPDATE review
SET ansnum = ansnum + 1
WHERE grpno = #{grpno} AND ansnum > #{ansnum}
